package br.unipe.mpl.banco.conta.dados.contas;import br.unipe.mpl.banco.contas.Conta;/** *  * @author jefferson * */public class RepositorioContasArray implements IRepositorioContas {		private Conta[] contas;	private int indice;	public RepositorioContasArray(int tam) {		contas = new Conta[tam];	}		public void inserir(Conta conta) {		contas[indice] = conta;		indice = indice + 1;	}	@Override	public Conta procurar(String numero) {		Conta conta = null;		int i = this.getIndice(numero);		if (i == this.indice) {			System.out.println("Conta nao encontrada.");		} else {			conta = this.contas[i];		}		return conta;	}	@Override	public void remover(String numero) {		int i = this.getIndice(numero);		if (i == this.indice) {			System.out.println("Carro nao encontrado.");		} else {			this.indice = this.indice - 1;			this.contas[i] = this.contas[this.indice];			this.contas[this.indice] = null;		}	}	@Override	public void atualizar(Conta conta) {		int i = this.getIndice(conta.getNumero());		if (i == this.indice) {			System.out.println("Conta nao encontrada.");		} else {			this.contas[i] = conta;		}	}		/**	 * 	 * @param codigoLocacao	 * @return	 */	private int getIndice(String numero) {		String n;		boolean achou = false;		int i = 0;		while ((!achou) && (i < this.indice)) {			n = contas[i].getNumero();			if (n.equals(numero)) {				achou = true;			} else {				i = i + 1;			}		}		return i;	}	@Override	public boolean existe(String numero) {		int i = this.getIndice(numero);		return (i != this.indice);	}}